var util = require("./util.js")

this.me =  function(req, res, next){
	tpl_val = util.mk_tpl_val(req)
	tpl_val.select = {map: "select"}
	tpl_val.map = req.session.map
	tpl_val.comp = req.session.comp
	res.render("map", tpl_val)
}


this.search = function(req, res, next){
	cid = req.body.cid
	r.get(cid+":city:x", function(err, rep){
		if(rep == null)
			res.redirect("/world")
		else
			res.redirect("/world/"+cid)
	})
}
